// Procesar datos de ACREEDORES basado en la estructura real del Excel 
// Solo procesamos las columnas que NO est√°n marcadas con X roja 
const acreedoresData = items || []; 
let totalDeudaOrdinaria = 0; 
let totalDeudaPrivilegiada = 0; 
let totalCuotaMensual = 0; 
let acreedoresValidos = []; 

function parseNumber(val) { 
  if (val === null || val === undefined) return 0; 
  if (typeof val === 'number') return val; 
  let s = String(val).trim(); 
  if (!s) return 0; 

  // Limpiar s√≠mbolos comunes 
  s = s.replace(/‚Ç¨/g, '').replace(/\s+/g, ''); 

  // Manejar formato europeo (puntos como separadores de miles, comas como decimales) 
  if (/\d+\.\d+,\d+/.test(s)) { 
    s = s.replace(/\./g, '').replace(/,/g, '.'); 
  } else if (/\d+,\d+/.test(s) && !/\d+\.\d+/.test(s)) { 
    s = s.replace(/,/g, '.'); 
  } 

  s = s.replace(/[^0-9\.\-]/g, ''); 
  const n = parseFloat(s); 
  return isNaN(n) ? 0 : n; 
} 

// Mapeo de columnas seg√∫n la imagen del Excel (excluyendo las marcadas con X roja) 
// Columnas a incluir: ACREEDOR, PRODUCTO/TIPO DE DEUDA, DEUDA POR DEUDOR ORDINARIA, CUOTA/MES, PROCEDIMIENTOS JUDICIALES, Fecha solicitud, Fecha Vencimiento, CONTACTO 
function mapearColumnas(row) { 
  const keys = Object.keys(row); 
  const valores = keys.map(key => row[key]); 

  // Mapeo basado en posiciones esperadas del Excel 
  return { 
    acreedor: String(valores[0] || '').trim(), // Columna A: ACREEDOR 
    tipoDeuda: String(valores[1] || '').trim(), // Columna B: PRODUCTO/TIPO DE DEUDA 
    deudaOrdinaria: parseNumber(valores[2]), // Columna C: DEUDA POR DEUDOR ORDINARIA 
    deudaPrivilegiada: parseNumber(valores[3]), // Columna D: DEUDA PRIVILEGIADA 
    cuotaMensual: parseNumber(valores[4]), // Columna E: CUOTA/MES 
    // Saltamos columna F (% - marcada con X roja) 
    // Saltamos columna G (Garant√≠a real/personal - marcada con X roja) 
    // Saltamos columna H (Reserva de Dominio - marcada con X roja) 
    // Saltamos columna I (Expedientes de Apremio - marcada con X roja) 
    procedimientosJudiciales: String(valores[10] || '').trim(), // Columna K: PROCEDIMIENTOS JUDICIALES 
    fechaSolicitud: String(valores[11] || '').trim(), // Columna L: Fecha solicitud 
    fechaVencimiento: String(valores[12] || '').trim(), // Columna M: Fecha Vencimiento 
    contacto: String(valores[13] || '').trim() // Columna N: CONTACTO 
  }; 
} 

// Procesar cada fila del Excel 
for (let i = 0; i < acreedoresData.length; i++) { 
  const row = acreedoresData[i].json || {}; 
  const keys = Object.keys(row); 

  // Ignorar filas vac√≠as o que contienen encabezados/totales 
  const firstValue = keys.length > 0 ? String(row[keys[0]] || '').trim() : ''; 
  if (!firstValue || 
      firstValue.toUpperCase().includes('TOTAL') || 
      firstValue.toUpperCase().includes('PASIVO') || 
      firstValue.toUpperCase().includes('CONCEPTOS') || 
      firstValue.toUpperCase().includes('ACREEDOR') || 
      firstValue.toUpperCase().includes('LEASING') || 
      firstValue.toUpperCase().includes('RESERVA') || 
      firstValue.toUpperCase().includes('DETERMINAR')) { 
    continue; 
  } 

  // Mapear columnas seg√∫n la estructura deseada 
  const datos = mapearColumnas(row); 

  // Solo procesar si hay un acreedor v√°lido 
  if (datos.acreedor && datos.acreedor.length > 0) { 
    // Calcular totales 
    totalDeudaOrdinaria += datos.deudaOrdinaria; 
    totalDeudaPrivilegiada += datos.deudaPrivilegiada; 
    totalCuotaMensual += datos.cuotaMensual; 

    // Parsear informaci√≥n de contacto (formato: NIF:xxx; Domicilio: xxx; tlf: xxx; email: xxx) 
    const contactoInfo = datos.contacto; 
    let nif = '', domicilio = '', telefono = '', email = ''; 

    if (contactoInfo) { 
      const nifMatch = contactoInfo.match(/NIF:([^;]+)/i); 
      const domicilioMatch = contactoInfo.match(/Domicilio:([^;]+)/i); 
      const tlfMatch = contactoInfo.match(/tlf:([^;]+)/i); 
      const emailMatch = contactoInfo.match(/email:([^;]+)/i); 

      nif = nifMatch ? nifMatch[1].trim() : ''; 
      domicilio = domicilioMatch ? domicilioMatch[1].trim() : ''; 
      telefono = tlfMatch ? tlfMatch[1].trim() : ''; 
      email = emailMatch ? emailMatch[1].trim() : ''; 
    } 

    // Crear objeto con los datos filtrados 
    const acreedorData = { 
      nombre: datos.acreedor, 
      tipo_deuda: datos.tipoDeuda, 
      deuda_ordinaria: datos.deudaOrdinaria, 
      deuda_privilegiada: datos.deudaPrivilegiada, 
      cuota_mensual: datos.cuotaMensual, 
      procedimientos_judiciales: datos.procedimientosJudiciales, 
      fecha_solicitud: datos.fechaSolicitud, 
      fecha_vencimiento: datos.fechaVencimiento, 
      nif: nif, 
      domicilio: domicilio, 
      telefono: telefono, 
      email: email, 
      contacto_completo: contactoInfo, 
      fila: i + 1, 
      raw: row 
    }; 

    acreedoresValidos.push(acreedorData); 
  } 
} 

const filename = $('Microsoft OneDrive Trigger').first()?.json?.name || (items[0]?.json?.name) || ''; 
const totalDeuda = totalDeudaOrdinaria + totalDeudaPrivilegiada; 

