 const aiNode = $('Generate Legal ACREEDORES Text').first(); 
 const aiJson = aiNode ? aiNode.json : (items[0] ? items[0].json : null); 
 let aiResponseRaw = extractAIContent(aiJson) || ''; 

 // Formatear n√∫meros para mostrar con separador de miles y s√≠mbolo de euro (formato espa√±ol) 
 function formatCurrency(value) { 
   if (value === null || value === undefined || value === 0) return '0,00 ‚Ç¨'; 

   // Convertir a n√∫mero si es string 
   const num = typeof value === 'string' ? parseFloat(value) : value; 
   if (isNaN(num)) return '0,00 ‚Ç¨'; 

   // Formato espa√±ol: separador de miles punto, decimales coma 
   const formatted = num.toLocaleString('es-ES', { 
