   return formatted + ' ‚Ç¨'; 
 } 

 // Procesar la respuesta de la IA para limpiar y formatear 
 const processedData = $('Process ACREEDORES Data').first()?.json || {}; 

 // Limpiar la respuesta de la IA 
 let acreedoresLines = aiResponseRaw.trim(); 
 // Reemplazar caracteres problem√°ticos 
 acreedoresLines = acreedoresLines.replace(/\?/g, ' '); 
 acreedoresLines = acreedoresLines.replace(/ÔøΩ/g, ''); 

 // La respuesta de la IA ya viene en el formato correcto: 
