 // Solo necesitamos limpiarla y usarla directamente 

 // Crear secci√≥n de totales formateada (con formato espa√±ol) 
 const totalesSection = ` 

 TOTALES: 
 Deuda Ordinaria: ${formatCurrency(processedData.total_deuda_ordinaria)} 
 Deuda Privilegiada: ${formatCurrency(processedData.total_deuda_privilegiada)} 
 Cuota Mensual: ${formatCurrency(processedData.total_cuota_mensual)}`; 

 // Construir el documento completo con el formato EXACTO requerido 
 const documentoCompleto = `RELACI√ìN DE ACREEDORES 

 En cumplimiento de lo dispuesto en la Ley Concursal, el deudor presenta la relaci√≥n de acreedores con expresi√≥n de identidad, domicilio, direcci√≥n electr√≥nica (si procede), cuant√≠a y vencimiento de los cr√©ditos, garant√≠as constituidas y, en su caso, los procedimientos judiciales iniciados y el estado de los mismos. 

 ${acreedoresLines} 
 ${totalesSection} 

 PASIVO TOTAL: ${formatCurrency(processedData.total_deuda)}`; 

 // Crear nombre de archivo con timestamp 
 const timestamp = new Date().toISOString().replace(/[:.]/g, '-').slice(0,19); 
