// Generador simple de DOCX SIN dependencias externas
const documentoCompleto = (items[0].json.documento_completo || '').replace(/\?/g, ' ');
const outputFilename = items[0].json.output_filename || 'Relacion_Acreedores.docx';

// Funci√≥n para crear un documento DOCX b√°sico
function createSimpleDocx(text) {
  // Crear un archivo DOCX b√°sico (formato Office Open XML)
  // Estructura m√≠nima de un DOCX

  // Contenido del documento
  const documentText = text.split('\n').map(line => {
    if (line.trim() === '') {
      return '<w:p><w:r><w:t></w:t></w:r></w:p>';
    } else {
      return `<w:p><w:r><w:t>${escapeXml(line)}</w:t></w:r></w:p>`;
    }
  }).join('');

  // Archivos XML necesarios para un DOCX b√°sico
  const files = {
    '[Content_Types].xml': 
      '<?xml version="1.0" encoding="UTF-8" standalone="yes"?>\n' +
      '<Types xmlns="http://schemas.openxmlformats.org/package/2006/content-types">\n' +
      '  <Default Extension="rels" ContentType="application/vnd.openxmlformats-package.relationships+xml"/>\n' +
      '  <Default Extension="xml" ContentType="application/xml"/>\n' +
      '  <Override PartName="/word/document.xml" ContentType="application/vnd.openxmlformats-officedocument.wordprocessingml.document.main+xml"/>\n' +
      '</Types>',

    '_rels/.rels': 
      '<?xml version="1.0" encoding="UTF-8" standalone="yes"?>\n' +
      '<Relationships xmlns="http://schemas.openxmlformats.org/package/2006/relationships">\n' +
      '  <Relationship Id="rId1" Type="http://schemas.openxmlformats.org/officeDocument/2006/relationships/officeDocument" Target="word/document.xml"/>\n' +
      '</Relationships>',

    'word/document.xml': 
      '<?xml version="1.0" encoding="UTF-8" standalone="yes"?>\n' +
      '<w:document xmlns:w="http://schemas.openxmlformats.org/wordprocessingml/2006/main">\n' +
      '  <w:body>\n' +
      documentText +
      '  </w:body>\n' +
      '</w:document>',

    'word/_rels/document.xml.rels': 
      '<?xml version="1.0" encoding="UTF-8" standalone="yes"?>\n' +
      '<Relationships xmlns="http://schemas.openxmlformats.org/package/2006/relationships">\n' +
      '</Relationships>'
  };

  return createZip(files);
}

// Funci√≥n para escapar caracteres especiales en XML
function escapeXml(unsafe) {
  if (!unsafe) return '';
  return unsafe
    .replace(/&/g, '&amp;')
    .replace(/</g, '&lt;')
    .replace(/>/g, '&gt;')
    .replace(/"/g, '&quot;')
    .replace(/'/g, '&apos;');
}

// Funci√≥n para crear un archivo ZIP (formato base de DOCX)
function createZip(files) {
  // Esta es una implementaci√≥n muy b√°sica de ZIP
  // En un entorno real, se usar√≠a una biblioteca como JSZip

  // Estructura b√°sica de un archivo ZIP
  const zipEntries = [];
  let centralDirectory = [];
  let offset = 0;

  // Agregar cada archivo al ZIP
  Object.keys(files).forEach(filename => {
    const content = files[filename];
    const contentBuffer = Buffer.from(content, 'utf8');
    const contentLength = contentBuffer.length;

    // Local file header
    const header = Buffer.from([
      0x50, 0x4B, 0x03, 0x04, // Firma
      0x0A, 0x00, // Versi√≥n
      0x00, 0x00, // Flags
      0x00, 0x00, // M√©todo de compresi√≥n (sin compresi√≥n)
      0x00, 0x00, 0x00, 0x00, // Fecha y hora
      0x00, 0x00, 0x00, 0x00, // CRC32
      ...intToBytes(contentLength, 4), // Tama√±o comprimido
      ...intToBytes(contentLength, 4), // Tama√±o sin comprimir
      ...intToBytes(filename.length, 2), // Longitud del nombre
      0x00, 0x00 // Longitud del campo extra
    ]);

    const filenameBuffer = Buffer.from(filename, 'utf8');

    zipEntries.push(Buffer.concat([header, filenameBuffer, contentBuffer]));

    // Central directory entry
    const cdEntry = Buffer.from([
      0x50, 0x4B, 0x01, 0x02, // Firma
      0x14, 0x00, // Versi√≥n
      0x0A, 0x00, // Versi√≥n necesaria
      0x00, 0x00, // Flags
      0x00, 0x00, // M√©todo de compresi√≥n
      0x00, 0x00, 0x00, 0x00, // Fecha y hora
      0x00, 0x00, 0x00, 0x00, // CRC32
      ...intToBytes(contentLength, 4), // Tama√±o comprimido
      ...intToBytes(contentLength, 4), // Tama√±o sin comprimir
      ...intToBytes(filename.length, 2), // Longitud del nombre
      0x00, 0x00, // Longitud del campo extra
      0x00, 0x00, // Longitud del comentario
      0x00, 0x00, // N√∫mero de disco
      0x00, 0x00, // Atributos internos
      0x00, 0x00, 0x00, 0x00, // Atributos externos
      ...intToBytes(offset, 4) // Offset del archivo local
    ]);

    centralDirectory.push(Buffer.concat([cdEntry, filenameBuffer]));
    offset += header.length + filenameBuffer.length + contentBuffer.length;
  });

  // End of central directory record
  const eocd = Buffer.from([
    0x50, 0x4B, 0x05, 0x06, // Firma
    0x00, 0x00, // N√∫mero de disco
    0x00, 0x00, // Disco donde comienza el directorio central
    ...intToBytes(centralDirectory.length, 2), // N√∫mero de entradas en este disco
    ...intToBytes(centralDirectory.length, 2), // N√∫mero total de entradas
    ...intToBytes(Buffer.concat(centralDirectory).length, 4), // Tama√±o del directorio central
    ...intToBytes(offset, 4), // Offset del directorio central
    0x00, 0x00 // Longitud del comentario
  ]);

  // Combinar todo
  return Buffer.concat([
    ...zipEntries,
    ...centralDirectory,
    eocd
  ]);
}

// Funci√≥n auxiliar para convertir un entero a bytes
function intToBytes(num, size) {
  const bytes = [];
  for (let i = 0; i < size; i++) {
    bytes.push(num & 0xFF);
    num = num >> 8;
  }
  return bytes;
}

// Crear el documento DOCX
const docxBuffer = createSimpleDocx(documentoCompleto);

return [{
  json: {
    docx_generated: true,
    document_content: documentoCompleto,
    output_filename: outputFilename,
    source_file: items[0].json.source_file || '',
    total_deuda: items[0].json.total_deuda || null,
    total_acreedores: items[0].json.total_acreedores || null,
    processing_date: items[0].json.processing_date || new Date().toISOString()
  },
  binary: {
    data: {
      data: docxBuffer.toString('base64'),
      mimeType: 'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
      fileName: outputFilename
    }
  }
}];

